### .Net

``` csharp
using System;
using System.IO;
using System.Net.Http;
using System.Security.Cryptography;
using System.Threading.Tasks;

class Program
{
    static async Task Main()
    {
        // Statik URL veya yerel dosya yolu belirleyin
        //string input = "https://siberci.com/wp-content/uploads/2019/08/siberci2-e1563710807833.png";
        string input = "../ Ivy Internet.png";

        if (Uri.IsWellFormedUriString(input, UriKind.Absolute))
        {
            // URL'den checksum hesapla
            (string base64Checksum, long byteSize) = await GetChecksumFromUrlAsync(input);
            Console.WriteLine("Checksum (Base64): " + base64Checksum);
            Console.WriteLine("Byte Size: " + byteSize);
        }
        else if (File.Exists(input))
        {
            // Yerel dosyadan checksum hesapla
            (string base64Checksum, long byteSize) = GetFileChecksumAndSize(input);
            Console.WriteLine("Checksum (Base64): " + base64Checksum);
            Console.WriteLine("Byte Size: " + byteSize);
        }
        else
        {
            Console.WriteLine("Geçersiz URL veya dosya yolu.");
            return;
        }
    }

    static async Task<(string, long)> GetChecksumFromUrlAsync(string url)
    {
        // Geçici bir dosya yolu oluşturun
        string tempFilePath = Path.GetTempFileName();

        // Dosyayı URL'den indir
        await DownloadFileFromUrlAsync(url, tempFilePath);

        // Dosya checksum ve byte boyutunu hesapla
        (string base64Checksum, long byteSize) = GetFileChecksumAndSize(tempFilePath);

        // Geçici dosyayı temizle
        File.Delete(tempFilePath);

        return (base64Checksum, byteSize);
    }

    static async Task DownloadFileFromUrlAsync(string url, string filePath)
    {
        using (HttpClient client = new HttpClient())
        {
            byte[] fileBytes = await client.GetByteArrayAsync(url);
            await File.WriteAllBytesAsync(filePath, fileBytes);
        }
    }

    static (string, long) GetFileChecksumAndSize(string filePath)
    {
        // Dosyanın byte boyutunu al
        long byteSize = new FileInfo(filePath).Length;

        // MD5 hash hesapla
        using (MD5 md5 = MD5.Create())
        using (FileStream stream = File.OpenRead(filePath))
        {
            byte[] hash = md5.ComputeHash(stream);
            // Hash'i Base64 formatına çevir
            string base64Checksum = Convert.ToBase64String(hash);
            return (base64Checksum, byteSize);
        }
    }
}

